mapper.insert:
insert into t_b_article(
id,uid,author,title,content,
category,type,tag,create_time,update_time,
audit_status
)
values(
#{id},#{uid},#{author},#{title},#{content},
#{category},#{type},#{tag},#{createTime},#{updateTime},
#{auditStatus}
)

mapper.update:
update t_b_article
title=#{title}, content=#{content}, category=#{category}, type=#{type}, tag=#{tag}, update_time=#{updateTime}, audit_status=#{auditStatus}
where id=#{id}

mapper.select:
select art.*
from t_b_article art
where art.uid=#{uid}
limit #{limit} offset #{offset}
