mapper.insert:
insert ignore into t_si_auth_menu(
auth_id,menu_id
)
values(
#{authId},#{menuId}
)

mapper.select:
select *
from t_si_auth_menu
where auth_id=#{authId} and menu_id=#{menuId}

mapper.update:
update t_si_auth_menu

    auth_id=#{newAuthId}

where auth_id=#{authId} and menu_id=#{menuId}
